#include <stdio.h>

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    int l[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &l[i]);
    }
    int max_len = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            int sum = l[i] + l[j];
            for (int m = j + 1; m < n; m++) {
                int temp_sum = sum + l[m];
                if (temp_sum > max_len && k > 3) {
                    max_len = temp_sum;
                }
                if (k == 3 && temp_sum > max_len) {
                    max_len = temp_sum;
                }
                if (k > 3 && m + 1 == n) {
                    break;
                }
            }
        }
    }
    printf("%d\n", max_len);
    return 0;
}
