import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int friends = scanner.nextInt();
        int cakeCount = scanner.nextInt();

        int totalCakes = 0;
        for (int i = 0; i < cakeCount; i++) {
            totalCakes += scanner.nextInt();
        }

        int totalPeople = friends + 1;
        int cakesPerPerson = totalCakes / totalPeople;
        int remainingCakes = totalCakes % totalPeople;

        System.out.println(cakesPerPerson + remainingCakes);
    }
}
