#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX 64

long long n, low, high;

void ask(long long x) {
    printf("? %lld\n", x);
    fflush(stdout);
}

int getAns() {
    char ans[2];
    scanf("%s", ans);
    return ans[0] == 'Y';
}

void solve() {
    long long i, j, mid;
    for (i = 1, j = 1e18; i <= j; i = mid + 1, j = mid) {
        mid = (i + j) / 2;
        ask(mid);
        if (getAns()) {
            low = mid;
            j = mid - 1;
        } else {
            i = mid + 1;
        }
    }
    ask(low);
    if (getAns()) {
        printf("! %lld\n", low);
    } else {
        printf("! %lld\n", low + 1);
    }
    fflush(stdout);
    exit(0);
}

int main() {
    solve();
    return 0;
}
