import java.io.*;
import java.util.*;

public class Main {
    static final int MAX = 1000000000;
    static final int MAX2 = 1000000000000000000L;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        long l = 1, r = MAX2;
        while (l <= r) {
            long mid = (l + r) / 2;
            String s = String.valueOf(mid);
            String t = String.valueOf(MAX);
            if (mid <= MAX && s.compareTo(t) <= 0) {
                pw.println("? " + mid);
                pw.flush();
                String ans = br.readLine();
                if (ans.equals("Y")) {
                    r = mid - 1;
                } else {
                    l = mid + 1;
                }
            } else if (mid > MAX && s.compareTo(t) > 0) {
                pw.println("? " + mid);
                pw.flush();
                String ans = br.readLine();
                if (ans.equals("Y")) {
                    l = mid + 1;
                } else {
                    r = mid - 1;
                }
            } else {
                l = mid + 1;
                r = mid - 1;
            }
        }
        pw.println("! " + r);
        pw.flush();
        pw.close();
    }
}
