#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define N 16
#define INF INT_MAX

int n, m;
int g[N][N];
int d[N][N];
int p[N][N];

void dfs(int u, int v, int w) {
    p[u][v] = w;
    for (int i = 1; i <= n; i++) {
        if (g[u][i] && p[u][i] == 0) {
            dfs(u, i, w);
        }
    }
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 1; i <= m; i++) {
        int u, v, c;
        scanf("%d %d %d", &u, &v, &c);
        g[u][v] = g[v][u] = c;
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            d[i][j] = g[i][j] + (i == j ? 0 : INF);
        }
    }
    for (int k = 1; k <= n; k++) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                d[i][j] = (d[i][j] < d[i][k] + d[k][j] ? d[i][j] : d[i][k] + d[k][j]);
            }
        }
    }
    dfs(n, 1, 0);
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            if (i != j && p[i][j] == 0) {
                ans += g[i][j];
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
