import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int p = scanner.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
        }
        long sum = 0;
        for (int i = 0; i < n; i++) {
            sum += a[i];
        }
        long result = 0;
        if (p == 0) {
            if (sum % 2 == 0) {
                result = 1;
            }
            for (int i = 0; i < n; i++) {
                if (a[i] % 2 == 1) {
                    result += 1;
                }
            }
            result = (long) Math.pow(2, result);
            result = (long) Math.pow(2, n) - result - 1;
        } else {
            if (sum % 2 == 1) {
                result = 1;
            }
            for (int i = 0; i < n; i++) {
                if (a[i] % 2 == 0) {
                    result += 1;
                }
            }
            result = (long) Math.pow(2, result);
            result = (long) Math.pow(2, n) - result - 1;
        }
        System.out.println(result);
    }
}
