#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 200005

int n, m;
int a[MAXN];
int x[MAXN], y[MAXN];
int cnt[MAXN];
int ans;

void update(int pos, int val) {
    while (pos <= n) {
        cnt[pos] = val;
        pos += (pos & -pos);
    }
}

int query(int pos) {
    int res = 0;
    while (pos) {
        res = max(res, cnt[pos]);
        pos -= (pos & -pos);
    }
    return res;
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
        update(i, a[i]);
    }
    for (int i = 1; i <= m; i++) {
        scanf("%d %d", &x[i], &y[i]);
        ans = 0;
        for (int j = 1; j <= n; j++) {
            cnt[j] = 0;
        }
        for (int j = 1; j <= n; j++) {
            cnt[j] = query(j);
        }
        int mx = 0;
        for (int j = 1; j <= n; j++) {
            if (cnt[j] > mx) {
                mx = cnt[j];
            }
        }
        int res = 0;
        for (int j = 1; j <= n; j++) {
            if (a[j] == mx) {
                res++;
            }
        }
        if (res == n) {
            printf("%d\n", 0);
        } else {
            int pos = 0;
            for (int j = 1; j <= n; j++) {
                if (a[j] == mx) {
                    pos = j;
                    break;
                }
            }
            int l = 1, r = n;
            while (l < r) {
                int mid = (l + r) / 2;
                int flag = 0;
                for (int j = 1; j <= n; j++) {
                    if (cnt[j] == mid) {
                        flag = 1;
                        break;
                    }
                }
                if (flag) {
                    r = mid;
                } else {
                    l = mid + 1;
                }
            }
            if (l == mx) {
                printf("%d\n", res - 1);
            } else {
                printf("%d\n", res);
            }
        }
    }
    return 0;
}
