#include <iostream>
#include <vector>
#include <algorithm>
#include <set>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<int> x(m), y(m);
    for (int i = 0; i < m; i++) {
        cin >> x[i] >> y[i];
        x[i]--;
    }

    vector<int> cnt(n + 1, 0);
    for (int i = 0; i < n; i++) {
        cnt[a[i]]++;
    }

    set<int> s;
    for (int i = 1; i <= n; i++) {
        if (cnt[i] > 0) {
            s.insert(i);
        }
    }

    vector<int> ans(m);
    for (int i = 0; i < m; i++) {
        int x = x[i], y = y[i];
        cnt[a[x]]--;
        if (a[x] == a[y]) {
            cnt[a[y]]--;
        }
        a[x] = y;
        if (a[x] == a[y]) {
            cnt[a[y]]++;
        }
        cnt[a[x]]++;

        int mx = 0;
        for (int j = 1; j <= n; j++) {
            if (cnt[j] > 0) {
                s.insert(j);
                mx = max(mx, j);
            }
        }

        int res = 0;
        for (int j = 1; j <= n; j++) {
            if (s.find(j) != s.end()) {
                res += n - mx;
                mx = max(mx, j);
            }
        }

        ans[i] = res;
    }

    for (int i = 0; i < m; i++) {
        cout << ans[i] << endl;
    }

    return 0;
}
