import sys
input = sys.stdin.readline

N, M = map(int, input().split())
A = [0] + list(map(int, input().split()))
X = [0] * (M + 1)
Y = [0] * (M + 1)
for i in range(1, M + 1):
    X[i], Y[i] = map(int, input().split())

class BIT:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (n + 1)

    def update(self, i, x):
        while i <= self.n:
            self.tree[i] += x
            i += i & -i

    def query(self, i):
        res = 0
        while i:
            res += self.tree[i]
            i -= i & -i

    def range_query(self, l, r):
        return self.query(r) - self.query(l - 1)

def solve():
    bit = BIT(N)
    cnt = [0] * (N + 1)
    for i in range(1, N + 1):
        bit.update(A[i], 1)
        cnt[A[i]] += 1
    ans = 0
    for i in range(1, M + 1):
        bit.update(X[i], -1)
        bit.update(Y[i], 1)
        cnt[Y[i]] += 1
        cnt[X[i]] -= 1
        ans = max(ans, N - bit.range_query(1, N))
    print(ans - cnt[1])

for _ in range(1, M + 1):
    solve()
