#include <stdio.h>
#include <stdlib.h>

#define N 100000

int n;
int e[N][2];
int par[N];
int sz[N];

void dfs(int u, int p) {
    par[u] = p;
    sz[u] = 1;
    for (int i = 0; i < 2; i++) {
        int v = e[u][i];
        if (v != p) {
            dfs(v, u);
            sz[u] += sz[v];
        }
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n - 1; i++) {
        scanf("%d %d", &e[i][0], &e[i][1]);
        e[i][0]--;
        e[i][1]--;
    }
    dfs(0, -1);
    int root = 0;
    for (int i = 0; i < n; i++) {
        if (sz[i] > sz[root]) {
            root = i;
        }
    }
    int p = par[root];
    int cnt = 0;
    for (int i = 0; i < n - 1; i++) {
        if (e[i][0] == p || e[i][1] == p) {
            cnt++;
        }
    }
    if (cnt & 1) {
        printf("Alice\n");
    } else {
        printf("Bob\n");
    }
    return 0;
}
