#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n;
vector<int> g[N];
int par[N], sz[N];

void dfs(int u, int p) {
    par[u] = p;
    sz[u] = 1;
    for (int v : g[u]) {
        if (v != p) {
            dfs(v, u);
            sz[u] += sz[v];
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        g[u].push_back(v);
        g[v].push_back(u);
    }

    dfs(1, 0);

    int ans = 0;
    for (int i = 1; i <= n; i++) {
        if (g[i].size() == 1 && g[i][0] == 1) {
            ans = 1;
            break;
        }
    }

    if (ans) {
        cout << "Alice\n";
    } else {
        int cnt = 0;
        for (int i = 1; i <= n; i++) {
            if (g[i].size() == 1 && g[i][0] == 1) continue;
            if (sz[i] % 2 == 0) cnt++;
        }
        if (cnt % 2 == 0) cout << "Bob\n";
        else cout << "Alice\n";
    }

    return 0;
}
