#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 1e5 + 5;
const int MAXH = 200;

struct Piece {
    int A, B, C, D;
};

bool check(vector<Piece>& pieces, int h) {
    vector<int> dp(h + 1, 0);
    dp[0] = 1;
    for (auto& piece : pieces) {
        for (int i = h; i >= piece.A; --i) {
            if (dp[i - piece.A] && i - piece.C >= 0) {
                dp[i] = 1;
            }
        }
        for (int i = h; i >= piece.B; --i) {
            if (dp[i - piece.B] && i - piece.D >= 0) {
                dp[i] = 1;
            }
        }
    }
    return dp[h];
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, h;
    cin >> n >> h;

    vector<Piece> pieces(n);
    for (auto& piece : pieces) {
        cin >> piece.A >> piece.B >> piece.C >> piece.D;
    }

    sort(pieces.begin(), pieces.end(), [](const Piece& a, const Piece& b) {
        return a.A + a.B < b.A + b.B;
    });

    for (int i = 1; i <= h; ++i) {
        if (check(pieces, i)) {
            cout << "YES" << endl;
            return 0;
        }
    }

    cout << "NO" << endl;

    return 0;
}
