import java.io.*;
import java.util.*;

public class Main {
    static class Piece {
        int a, b, c, d;
        int h;

        public Piece(int a, int b, int c, int d, int h) {
            this.a = a;
            this.b = b;
            this.c = c;
            this.d = d;
            this.h = h;
        }
    }

    static class Event implements Comparable<Event> {
        int t, id;

        public Event(int t, int id) {
            this.t = t;
            this.id = id;
        }

        @Override
        public int compareTo(Event o) {
            return Integer.compare(t, o.t);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int h = Integer.parseInt(st.nextToken());
        Piece[] pieces = new Piece[n];
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            pieces[i] = new Piece(Integer.parseInt(st.nextToken()), Integer.parseInt(st.nextToken()),
                    Integer.parseInt(st.nextToken()), Integer.parseInt(st.nextToken()), h);
        }
        Arrays.sort(pieces, Comparator.comparingInt(p -> p.a));
        int[] dp = new int[h + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;
        List<Event> events = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            int t = pieces[i].a;
            events.add(new Event(t, i));
            t += pieces[i].c;
            events.add(new Event(t, i));
            t += pieces[i].d;
            events.add(new Event(t, i));
        }
        Collections.sort(events);
        int[] max = new int[n];
        Arrays.fill(max, Integer.MIN_VALUE);
        for (Event e : events) {
            int id = e.id;
            int t = e.t;
            max[id] = Math.max(max[id], t);
            for (int j = h; j >= 0; j--) {
                dp[j] = Math.min(dp[j], dp[Math.max(0, j - pieces[id].a)] + pieces[id].c);
                dp[j] = Math.min(dp[j], dp[Math.max(0, j - pieces[id].b)] + pieces[id].d);
            }
        }
        boolean ok = true;
        for (int i = 0; i < n; i++) {
            if (max[i] < pieces[i].a + pieces[i].c + pieces[i].d) {
                ok = false;
                break;
            }
        }
        System.out.println(ok ? "YES" : "NO");
    }
}
