#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

int n, m, k;
int a[21][21], b[21][21], c[21][21];
int dp[21][1 << 21];

int dfs(int i, int mask) {
    if (i == m) return 1;
    if (dp[i][mask] != -1) return dp[i][mask];
    int res = 0;
    for (int j = 0; j < (1 << n); j++) {
        if ((mask & j) == mask) {
            int flag = 1;
            for (int l = 0; l < k; l++) {
                if (a[l][i] == i + 1 && b[l][i] + 1 == c[l][i] && (j & (1 << b[l][i])) == 0) {
                    flag = 0;
                    break;
                }
                if (a[l][i] == i + 1 && b[l][i] + 1 == c[l][i] && (j & (1 << (b[l][i] + 1))) != 0) {
                    flag = 0;
                    break;
                }
                if (a[l][i] == i + 1 && b[l][i] + 1 != c[l][i] && (j & (1 << b[l][i])) != 0) {
                    flag = 0;
                    break;
                }
                if (a[l][i] == i + 1 && b[l][i] + 1 != c[l][i] && (j & (1 << (b[l][i] + 1))) == 0) {
                    flag = 0;
                    break;
                }
                if (a[l][i] != i + 1 && b[l][i] + 1 == c[l][i] && (j & (1 << b[l][i])) == 0) {
                    flag = 0;
                    break;
                }
                if (a[l][i] != i + 1 && b[l][i] + 1 == c[l][i] && (j & (1 << (b[l][i] + 1))) != 0) {
                    flag = 0;
                    break;
                }
                if (a[l][i] != i + 1 && b[l][i] + 1 != c[l][i] && (j & (1 << b[l][i])) != 0) {
                    flag = 0;
                    break;
                }
                if (a[l][i] != i + 1 && b[l][i] + 1 != c[l][i] && (j & (1 << (b[l][i] + 1))) == 0) {
                    flag = 0;
                    break;
                }
            }
            if (flag) {
                res = (res + dfs(i + 1, j)) % MOD;
            }
        }
    }
    return dp[i][mask] = res;
}

int main() {
    scanf("%d %d %d", &n, &m, &k);
    for (int i = 0; i < k; i++) {
        scanf("%d %d %d", &a[i][0], &b[i][0], &c[i][0]);
    }
    memset(dp, -1, sizeof(dp));
    printf("%d\n", dfs(0, 0));
    return 0;
}
