#include <iostream>
#include <vector>
#include <algorithm>
#include <cstring>

using namespace std;

const int N = 25;
const int M = 25;
const int K = 100;
const int MOD = 1000000007;

int n, m, k;
int a[K], b[K], c[K];
int dp[N][M][1 << N];
int f[N][M];

int main() {
    cin >> n >> m >> k;
    for (int i = 0; i < k; i++) {
        cin >> a[i] >> b[i] >> c[i];
        a[i]--;
    }

    memset(dp, 0, sizeof(dp));
    dp[0][0][0] = 1;
    for (int i = 0; i < k; i++) {
        for (int j = 0; j <= m; j++) {
            for (int s = 0; s < (1 << n); s++) {
                if (dp[i][j][s] == 0) continue;
                for (int x = 0; x <= n; x++) {
                    int t = s | (1 << (x - 1));
                    if (x == n) {
                        f[j][x] = (f[j][x] + dp[i][j][s]) % MOD;
                    } else {
                        if (c[i] == 0 && (t & (1 << x)) == 0) {
                            dp[i + 1][j][t] = (dp[i + 1][j][t] + dp[i][j][s]) % MOD;
                        }
                        if (c[i] == 1 && (t & (1 << x)) == 0) {
                            dp[i + 1][j][t | (1 << x)] = (dp[i + 1][j][t | (1 << x)] + dp[i][j][s]) % MOD;
                        }
                    }
                }
            }
        }
    }

    for (int i = 0; i <= m; i++) {
        for (int j = 0; j <= n; j++) {
            f[i][j] = (f[i][j] + f[i][j - 1]) % MOD;
        }
    }

    int ans = 0;
    for (int i = 0; i <= m; i++) {
        ans = (ans + f[i][n]) % MOD;
    }

    cout << ans << endl;

    return 0;
}
