import sys
input = sys.stdin.readline

MOD = 10**9 + 7

def add(x, y):
    return (x + y) % MOD

def mul(x, y):
    return (x * y) % MOD

def pow(x, y):
    res = 1
    while y:
        if y & 1:
            res = mul(res, x)
        x = mul(x, x)
        y >>= 1
    return res

def solve():
    N, M, K = map(int, input().split())
    A = []
    B = []
    C = []
    for _ in range(K):
        a, b, c = map(int, input().split())
        A.append(a - 1)
        B.append(b - 1)
        C.append(c)

    dp = [[0] * (M + 1) for _ in range(1 << K)]
    dp[0][0] = 1

    for mask in range(1 << K):
        for i in range(M + 1):
            if dp[mask][i]:
                for j in range(K):
                    if (mask >> j) & 1:
                        continue
                    if C[j] == 0:
                        if B[j] + 1 >= N - 1:
                            continue
                        if A[j] == i:
                            dp[mask | (1 << j)][i] = add(dp[mask | (1 << j)][i], dp[mask][i])
                    else:
                        if B[j] + 1 >= N - 1:
                            continue
                        if A[j] == i:
                            dp[mask | (1 << j)][i + 1] = add(dp[mask | (1 << j)][i + 1], dp[mask][i])
                        elif A[j] == i + 1:
                            dp[mask | (1 << j)][i + 1] = add(dp[mask | (1 << j)][i + 1], dp[mask][i])

    ans = 0
    for i in range(M + 1):
        ans = add(ans, dp[(1 << K) - 1][i])

    print(ans)

solve()
