#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);
    int a[n + 1];
    for (int i = 1; i <= n + 1; i++) {
        scanf("%d", &a[i]);
    }

    int freq[100001] = {0};
    for (int i = 1; i <= n + 1; i++) {
        freq[a[i]]++;
    }

    int dp[n + 1] = {0};
    dp[1] = 1;
    for (int i = 1; i <= n + 1; i++) {
        for (int j = 1; j <= n; j++) {
            if (a[i] == j) {
                dp[j + 1] = (dp[j + 1] + dp[j]) % MOD;
                freq[j]--;
                if (freq[j] >= 0) {
                    dp[j + 1] = (dp[j + 1] + (1LL * dp[j] * freq[j]) % MOD) % MOD;
                }
            }
        }
    }

    for (int i = 1; i <= n + 1; i++) {
        printf("%d\n", dp[i]);
    }

    return 0;
}
