import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int[] fact, invFact;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int n = Integer.parseInt(br.readLine());
        StringTokenizer st = new StringTokenizer(br.readLine());
        int[] a = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }
        fact = new int[n + 1];
        invFact = new int[n + 1];
        fact[0] = 1;
        for (int i = 1; i <= n; i++) {
            fact[i] = (int) ((long) fact[i - 1] * i % MOD);
        }
        invFact[n] = pow(fact[n], MOD - 2);
        for (int i = n - 1; i >= 0; i--) {
            invFact[i] = (int) ((long) invFact[i + 1] * (i + 1) % MOD);
        }
        int[] dp = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            dp[i] = (int) ((long) fact[i] * invFact[i - 1] % MOD);
        }
        for (int i = 1; i <= n + 1; i++) {
            int[] cnt = new int[n + 1];
            for (int j = 1; j <= n; j++) {
                cnt[a[j]]++;
            }
            int ans = 0;
            for (int j = 1; j <= n; j++) {
                if (cnt[j] > 0) {
                    ans = (int) ((long) ans + (long) dp[cnt[j]] * fact[j] % MOD);
                    ans %= MOD;
                }
            }
            pw.println(ans);
            for (int j = 1; j <= n; j++) {
                cnt[a[j]]--;
            }
        }
        pw.close();
    }

    static int pow(int a, int b) {
        int res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = (int) ((long) res * a % MOD);
            }
            a = (int) ((long) a * a % MOD);
            b >>= 1;
        }
        return res;
    }
}
