#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n, m;
int a[MAXN];

int min(int a, int b) {
    return (a < b) ? a : b;
}

int main() {
    int i, j, k, x, y, z, ans, cnt;
    long long sum;

    scanf("%d %d", &n, &m);
    for (i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }

    ans = 0;
    for (i = 1; i < n; i++) {
        if (a[i] == a[i + 1]) {
            ans++;
        } else {
            if (a[i] < a[i + 1]) {
                for (j = a[i]; j < a[i + 1]; j++) {
                    ans++;
                }
            } else {
                for (j = a[i]; j > a[i + 1]; j--) {
                    ans++;
                }
            }
        }
    }

    for (i = 1; i <= m; i++) {
        cnt = 0;
        for (j = 1; j < n; j++) {
            if (a[j] == i) {
                cnt++;
            }
        }
        sum = 0;
        for (j = 1; j < n; j++) {
            if (a[j] == i) {
                sum += min(cnt, n - j);
                cnt = 0;
            } else if (a[j] < i) {
                sum += (i - a[j]);
            } else {
                sum += (a[j] - i);
            }
        }
        ans = min(ans, sum);
    }

    printf("%d\n", ans);

    return 0;
}
