#include <stdio.h>
#include <string.h>

#define N 200005
#define M 26

char s[N];
int n, l, r, len, cnt[M], ans[M];

void add(int *cnt, int c, int x) {
    cnt[c] += x;
    if (cnt[c] >= len) {
        cnt[c] = len;
    }
}

int main() {
    scanf("%s", s);
    n = strlen(s);
    len = n;
    while (len % 2 == 0) {
        len /= 2;
    }
    for (int i = 0; i < n; i++) {
        add(cnt, s[i] - 'a', 1);
    }
    for (int i = 0; i < 26; i++) {
        ans[i] = cnt[i];
    }
    while (l > len) {
        l -= len;
        r -= len;
        for (int i = 0; i < 26; i++) {
            add(cnt, i, ans[i]);
        }
        for (int i = 0; i < n; i++) {
            add(cnt, s[i] - 'a', -1);
        }
        for (int i = 0; i < 26; i++) {
            ans[i] = cnt[i];
        }
    }
    for (int i = 0; i < 26; i++) {
        printf("%d ", ans[i]);
    }
    return 0;
}
