#include <iostream>
#include <vector>
#include <queue>
#include <unordered_map>

using namespace std;

int main() {
    int n;
    cin >> n;
    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<int> count(n + 1, 0);
    count[1] = 1;
    for (int i = 0; i < n; i++) {
        count[a[i]]++;
    }

    queue<int> q;
    q.push(1);
    int step = 0;
    while (!q.empty()) {
        int size = q.size();
        for (int i = 0; i < size; i++) {
            int x = q.front();
            q.pop();
            if (x == 2) {
                cout << step - count[2] + 1 << endl;
                return 0;
            }
            if (count[x] > 0) {
                count[x]--;
                q.push(a[x - 1]);
            }
        }
        step++;
    }

    cout << -1 << endl;
    return 0;
}
