import java.util.*;

public class Main {
    static class Graph {
        Map<Integer, List<Integer>> graph;
        int[] inDegree;
        int[] minPress;

        public Graph(int n) {
            graph = new HashMap<>();
            inDegree = new int[n + 1];
            minPress = new int[n + 1];
            for (int i = 1; i <= n; i++) {
                graph.put(i, new ArrayList<>());
                minPress[i] = 0;
            }
        }

        public void addEdge(int u, int v) {
            graph.get(u).add(v);
            inDegree[v]++;
        }

        public void bfs(int start) {
            Queue<Integer> queue = new LinkedList<>();
            queue.offer(start);
            minPress[start] = 0;
            while (!queue.isEmpty()) {
                int u = queue.poll();
                for (int v : graph.get(u)) {
                    if (minPress[v] == 0) {
                        minPress[v] = minPress[u] + 1;
                        queue.offer(v);
                    } else {
                        minPress[u] = Math.min(minPress[u], minPress[v] + 1);
                    }
                }
            }
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        Graph graph = new Graph(n);
        for (int i = 1; i <= n; i++) {
            int a = scanner.nextInt();
            graph.addEdge(i, a);
        }
        graph.bfs(1);
        if (graph.minPress[2] == 0) {
            System.out.println(-1);
        } else {
            System.out.println(graph.minPress[2] - 1);
        }
    }
}
