#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = (res * a) % MOD;
        a = (a * a) % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    long long fac[N + M + 1], inv[N + M + 1];
    fac[0] = 1;
    for (int i = 1; i <= N + M; i++) fac[i] = (fac[i - 1] * i) % MOD;
    inv[N + M] = power(fac[N + M], MOD - 2);
    for (int i = N + M - 1; i >= 1; i--) inv[i] = (inv[i + 1] * (i + 1)) % MOD;
    long long res = fac[N + M];
    for (int i = 0; i <= N; i++) {
        res = (res * inv[i] % MOD * inv[N + M - i - 1] % MOD * inv[M]) % MOD;
    }
    printf("%lld\n", res);
    return 0;
}
