#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, m;
    cin >> n >> m;

    vector<long long> fac(n + m + 1);
    fac[0] = 1;
    for (int i = 1; i <= n + m; ++i) {
        fac[i] = fac[i - 1] * i % MOD;
    }

    long long res = fac[n + m];
    res = res * power(fac[n], MOD - 2) % MOD;
    res = res * power(fac[m], MOD - 2) % MOD;

    for (int i = 1; i <= n; ++i) {
        res = res * i % MOD;
    }

    for (int i = 1; i <= m; ++i) {
        res = res * i % MOD;
    }

    cout << res << endl;

    return 0;
}
