#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MAXN 100005

typedef struct {
    int x, y;
} Point;

int compare(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    if (p1->x == p2->x) {
        return p1->y - p2->y;
    }
    return p1->x - p2->x;
}

int main() {
    int n;
    scanf("%d", &n);
    Point points[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &points[i].x, &points[i].y);
    }
    qsort(points, n, sizeof(Point), compare);
    int x = points[0].x, y = points[0].y;
    int ans = 0;
    for (int i = 0; i < n; i++) {
        if (points[i].x == x && points[i].y == y) {
            continue;
        }
        ans += abs(points[i].x - x) + abs(points[i].y - y);
        x = points[i].x;
        y = points[i].y;
    }
    printf("%d\n", ans);
    return 0;
}
