#include <bits/stdc++.h>
using namespace std;

const int MAXN = 1e5 + 5;
int n;
pair<int, int> pos[MAXN];
int par[MAXN];
int rank[MAXN];

int find(int x) {
    if (par[x] == x) return x;
    return par[x] = find(par[x]);
}

void unionSet(int x, int y) {
    int rootX = find(x);
    int rootY = find(y);
    if (rootX == rootY) return;
    if (rank[rootX] < rank[rootY]) swap(rootX, rootY);
    par[rootY] = rootX;
    if (rank[rootX] == rank[rootY]) rank[rootX]++;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    cin >> n;
    for (int i = 0; i < n; i++) {
        cin >> pos[i].first >> pos[i].second;
    }

    sort(pos, pos + n);
    for (int i = 0; i < n; i++) {
        par[i] = i;
        rank[i] = 0;
    }

    long long ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            if (pos[j].first > pos[i].first) {
                int dist = abs(pos[j].first - pos[i].first) + abs(pos[j].second - pos[i].second);
                unionSet(i, j);
                ans += min(dist, abs(pos[j].first - pos[i].first));
            }
        }
    }

    cout << ans << endl;

    return 0;
}
