import sys
import heapq

input = sys.stdin.readline

def solve():
    n = int(input())
    points = []
    for _ in range(n):
        x, y = map(int, input().split())
        points.append((x, y))

    points.sort()

    parent = list(range(n))
    rank = [0] * n

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x != root_y:
            if rank[root_x] > rank[root_y]:
                parent[root_y] = root_x
            else:
                parent[root_x] = root_y
                if rank[root_x] == rank[root_y]:
                    rank[root_y] += 1

    edges = []
    for i in range(n - 1):
        x1, y1 = points[i]
        x2, y2 = points[i + 1]
        cost = abs(x2 - x1) + abs(y2 - y1)
        heapq.heappush(edges, (cost, i, i + 1))

    total_cost = 0
    while edges:
        cost, u, v = heapq.heappop(edges)
        if find(u) != find(v):
            total_cost += cost
            union(u, v)

    print(total_cost)

if __name__ == "__main__":
    solve()
