#include <bits/stdc++.h>
using namespace std;

const int MAXN = 1e5 + 5;
int n;
pair<int, int> p[MAXN];
int parent[MAXN];
int rank[MAXN];

int find(int x) {
    if (parent[x] != x) {
        parent[x] = find(parent[x]);
    }
    return parent[x];
}

void unionSet(int x, int y) {
    int rootX = find(x);
    int rootY = find(y);
    if (rootX != rootY) {
        if (rank[rootX] > rank[rootY]) {
            parent[rootY] = rootX;
        } else if (rank[rootX] < rank[rootY]) {
            parent[rootX] = rootY;
        } else {
            parent[rootY] = rootX;
            rank[rootX]++;
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    cin >> n;
    for (int i = 0; i < n; i++) {
        cin >> p[i].first >> p[i].second;
    }

    sort(p, p + n);
    for (int i = 0; i < n; i++) {
        parent[i] = i;
    }

    long long cost = 0;
    for (int i = 0; i < n; i++) {
        int x = p[i].first;
        int y = p[i].second;
        for (int j = i + 1; j < n; j++) {
            int x2 = p[j].first;
            int y2 = p[j].second;
            if (x == x2 && y == y2) {
                continue;
            }
            int dx = abs(x - x2);
            int dy = abs(y - y2);
            int d = min(dx, dy);
            int rootX = find(i);
            int rootY = find(j);
            if (rootX != rootY) {
                cost += d;
                unionSet(rootX, rootY);
            }
        }
    }

    cout << cost << endl;

    return 0;
}
