import sys
import heapq

input = sys.stdin.readline

def solve():
    n = int(input())
    points = []
    for _ in range(n):
        x, y = map(int, input().split())
        points.append((x, y))

    points.sort()

    parent = list(range(n))
    rank = [0] * n

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x != root_y:
            if rank[root_x] > rank[root_y]:
                parent[root_y] = root_x
            else:
                parent[root_x] = root_y
                if rank[root_x] == rank[root_y]:
                    rank[root_y] += 1

    edges = []
    for i in range(n):
        for j in range(i + 1, n):
            if (points[i][0] == points[j][0] and abs(points[i][1] - points[j][1]) == j - i) or \
               (points[i][1] == points[j][1] and abs(points[i][0] - points[j][0]) == j - i):
                heapq.heappush(edges, (abs(points[i][0] - points[j][0]) + abs(points[i][1] - points[j][1]), i, j))
            else:
                heapq.heappush(edges, (abs(points[i][0] - points[j][0]) + abs(points[i][1] - points[j][1]), i, j))

    total_cost = 0
    while edges:
        cost, u, v = heapq.heappop(edges)
        if find(u) != find(v):
            union(u, v)
            total_cost += cost

    print(total_cost)

if __name__ == "__main__":
    solve()
