#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Point {
    int x, y, id;
};

bool comparePoints(const Point& a, const Point& b) {
    if (a.x == b.x) {
        return a.y < b.y;
    }
    return a.x < b.x;
}

bool check(vector<Point>& points, int n) {
    vector<int> x(n + 1), y(n + 1);
    for (int i = 0; i < n; i++) {
        x[points[i].id] = points[i].x;
        y[points[i].id] = points[i].y;
    }

    sort(points.begin(), points.end(), comparePoints);

    int i = 0;
    while (i < n) {
        int j = i;
        while (j < n && x[points[j].id] == x[points[i].id]) {
            j++;
        }

        int k = i;
        while (k < j && y[points[k].id] == y[points[i].id]) {
            k++;
        }

        if (k >= j) {
            return false;
        }

        i = j;
    }

    return true;
}

int main() {
    int R, C, N;
    cin >> R >> C >> N;

    vector<Point> points(2 * N);
    for (int i = 0; i < 2 * N; i++) {
        cin >> points[i].x >> points[i].y;
        points[i].id = i / 2 + 1;
    }

    sort(points.begin(), points.end(), comparePoints);

    for (int i = 0; i < 2 * N; i++) {
        if (points[i].x > R || points[i].y > C) {
            cout << "NO" << endl;
            return 0;
        }
    }

    if (check(points, N)) {
        cout << "YES" << endl;
    } else {
        cout << "NO" << endl;
    }

    return 0;
}
