#include <stdio.h>
#include <stdlib.h>

#define MAXN 300005

int n, q;
int a[MAXN];

int cmp(int x, int y) {
    return a[x] < a[y];
}

void swap(int x, int y) {
    int temp = a[x];
    a[x] = a[y];
    a[y] = temp;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    scanf("%d", &q);
    for (int i = 1; i <= q; i++) {
        int x, y;
        scanf("%d %d", &x, &y);
        swap(x, y);
        int flag = 0;
        for (int j = 1; j <= n - 1; j++) {
            if (cmp(j, j + 1)) {
                flag = 1;
                break;
            }
        }
        if (!flag) {
            printf("%d\n", i);
            return 0;
        }
    }
    printf("-1\n");
    return 0;
}
