import sys
input = sys.stdin.readline

def cross(p1, p2, p3):
    return (p2[0] - p1[0]) * (p3[1] - p1[1]) - (p2[1] - p1[1]) * (p3[0] - p1[0])

def convex_hull(points):
    n = len(points)
    if n < 3:
        return points

    points.sort(key=lambda x: (x[0], x[1]))
    l = 0
    for i in range(1, n):
        if points[i][0] < points[l][0]:
            l = i

    p = l
    q = (p + 1) % n
    hull = [points[p]]
    for i in range(n):
        while cross(hull[-2], hull[-1], points[q]) < 0:
            hull.pop()
            q = (q + 1) % n
        hull.append(points[q])
        p = q
        q = (p + 1) % n
    return hull

def solve():
    R, C, N = map(int, input().split())
    points = []
    for _ in range(N):
        x1, y1, x2, y2 = map(int, input().split())
        points.append((x1, y1))
        points.append((x2, y2))

    points.sort(key=lambda x: (x[0], x[1]))
    x = [p[0] for p in points]
    y = [p[1] for p in points]
    if len(set(x)) != N or len(set(y)) != N:
        print("NO")
        return

    hull = convex_hull(points)
    for i in range(len(hull)):
        if hull[i][0] > R or hull[i][1] > C:
            print("NO")
            return
    print("YES")

if __name__ == "__main__":
    t = 1
    for _ in range(t):
        solve()
