#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MAXN 200005

typedef struct {
    int l, r;
} Query;

int cmp(const void *a, const void *b) {
    Query *x = (Query *)a;
    Query *y = (Query *)b;
    return x->l - y->l;
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    Query q[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &q[i].l, &q[i].r);
        q[i].l++;
    }
    qsort(q, n, sizeof(Query), cmp);
    int ans = 0;
    int l = 1, r = 0;
    for (int i = 0; i < n; i++) {
        if (q[i].l > r) {
            ans += q[i].l - r - 1;
            r = q[i].r;
        } else if (q[i].r < l) {
            ans += l - q[i].r;
            l = q[i].l;
        } else {
            l = q[i].l;
            r = q[i].r;
        }
    }
    if (l <= m) ans += m - l + 1;
    printf("%d\n", ans);
    return 0;
}
