import java.io.*;
import java.util.*;

public class Main {
    static class SegmentTree {
        int n;
        int[] tree;

        public SegmentTree(int[] a) {
            n = a.length;
            tree = new int[4 * n];
            build(0, n - 1, 0, a);
        }

        private void build(int l, int r, int i, int[] a) {
            if (l == r) {
                tree[i] = a[l];
                return;
            }
            int mid = (l + r) / 2;
            build(l, mid, 2 * i + 1, a);
            build(mid + 1, r, 2 * i + 2, a);
            tree[i] = Math.max(tree[2 * i + 1], tree[2 * i + 2]);
        }

        public int query(int l, int r, int i, int lx, int rx) {
            if (l > r) return 0;
            if (lx >= l && rx <= r) return tree[i];
            int mid = (lx + rx) / 2;
            return Math.max(query(l, r, 2 * i + 1, lx, mid), query(l, r, 2 * i + 2, mid + 1, rx));
        }

        public void update(int i, int val, int l, int r, int ind) {
            if (l == r) {
                tree[i] = val;
                return;
            }
            int mid = (l + r) / 2;
            if (ind <= mid) update(2 * i + 1, val, l, mid, ind);
            else update(2 * i + 2, val, mid + 1, r, ind);
            tree[i] = Math.max(tree[2 * i + 1], tree[2 * i + 2]);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());
        int[] a = new int[n];
        int[] b = new int[n];
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken());
            b[i] = Integer.parseInt(st.nextToken());
        }
        SegmentTree stree = new SegmentTree(a);
        int[] c = new int[m + 1];
        for (int i = 0; i < n; i++) {
            int x = stree.query(a[i], b[i], 0, 0, m);
            c[x] = 1;
        }
        int ans = 0;
        for (int i = 0; i <= m; i++) {
            ans += c[i];
            if (c[i] == 1) {
                int l = i;
                int r = m;
                while (l <= r) {
                    int mid = (l + r) / 2;
                    if (stree.query(i, mid, 0, 0, m) >= i) {
                        r = mid - 1;
                    } else {
                        l = mid + 1;
                    }
                }
                stree.update(i, l, 0, m, l);
            }
        }
        System.out.println(m - ans);
    }
}
