#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);

    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }

    int sum = 0;
    for (int i = 0; i < n; i++) {
        sum += a[i];
    }

    if (sum > n * (n - 1)) {
        printf("No\n");
        return 0;
    }

    int count = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] == n - 1) {
            count++;
        }
    }

    if (count > 1) {
        printf("No\n");
        return 0;
    }

    int sum2 = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] != n - 1) {
            sum2 += a[i];
        }
    }

    if (sum2 < n - 1) {
        printf("No\n");
        return 0;
    }

    printf("Yes\n");
    return 0;
}
