#include <iostream>
#include <vector>
#include <string>

int main() {
    int H, W, h, w;
    std::cin >> H >> W >> h >> w;

    if (h * w > H * W) {
        std::cout << "No" << std::endl;
    } else {
        std::cout << "Yes" << std::endl;
        std::vector<std::vector<int>> matrix(H, std::vector<int>(W, 0));

        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (i < h && j < w) {
                    matrix[i][j] = -1;
                } else if (i < h) {
                    matrix[i][j] = 1;
                } else if (j < w) {
                    matrix[i][j] = 2;
                } else {
                    matrix[i][j] = 3;
                }
            }
        }

        for (const auto& row : matrix) {
            for (int val : row) {
                std::cout << val << " ";
            }
            std::cout << std::endl;
        }
    }

    return 0;
}
