import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int H = scanner.nextInt();
        int W = scanner.nextInt();
        int h = scanner.nextInt();
        int w = scanner.nextInt();

        if (h * w > H * W) {
            System.out.println("No");
        } else {
            System.out.println("Yes");
            for (int i = 1; i <= H; i++) {
                for (int j = 1; j <= W; j++) {
                    if (i <= h && j <= w) {
                        System.out.print(-1 + " ");
                    } else if (i > h && j > w) {
                        System.out.print(1 + " ");
                    } else {
                        System.out.print(0 + " ");
                    }
                }
                System.out.println();
            }
        }
    }
}
