#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);

    int a[n], b[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &b[i]);
    }

    int xor_a = 0;
    for (int i = 0; i < n; i++) {
        xor_a ^= a[i];
    }

    int xor_b = 0;
    for (int i = 0; i < n; i++) {
        xor_b ^= b[i];
    }

    if (xor_a != xor_b) {
        printf("-1\n");
        return 0;
    }

    int count = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] != b[i]) {
            count++;
        }
    }

    int ans = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] != xor_a) {
            a[i] = xor_a;
            ans++;
            count--;
            if (count == 0) {
                break;
            }
        }
    }

    if (count > 0) {
        printf("-1\n");
    } else {
        printf("%d\n", ans);
    }

    return 0;
}
