#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> a(n), b(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> a[i];
    }
    for (int i = 0; i < n; ++i) {
        std::cin >> b[i];
    }

    int xor_a = 0;
    for (int i = 0; i < n; ++i) {
        xor_a ^= a[i];
    }

    int xor_b = 0;
    for (int i = 0; i < n; ++i) {
        xor_b ^= b[i];
    }

    if (xor_a != xor_b) {
        std::cout << -1 << std::endl;
        return 0;
    }

    int ans = 0;
    for (int i = 0; i < n; ++i) {
        if (a[i] != b[i]) {
            ans += 1;
            a[i] = xor_a;
            int xor_c = 0;
            for (int j = 0; j < n; ++j) {
                xor_c ^= a[j];
            }
            if (xor_c != xor_b) {
                ans = -1;
                break;
            }
        }
    }

    std::cout << ans << std::endl;
    return 0;
}
