import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] a = new int[n];
        int[] b = new int[n];
        int xor = 0;
        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
            xor ^= a[i];
        }
        for (int i = 0; i < n; i++) {
            b[i] = scanner.nextInt();
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] != b[i]) {
                if (xor != b[i]) {
                    System.out.println(-1);
                    return;
                }
                ans++;
                a[i] = xor;
                xor ^= a[i];
            }
        }
        System.out.println(ans);
    }
}
