import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
        }
        int q = scanner.nextInt();
        int[] x = new int[q];
        int[] y = new int[q];
        for (int i = 0; i < q; i++) {
            x[i] = scanner.nextInt();
            y[i] = scanner.nextInt();
        }
        int count = 0;
        boolean sorted = isSorted(a);
        while (!sorted) {
            for (int i = 0; i < q; i++) {
                int temp = a[x[i] - 1];
                a[x[i] - 1] = a[y[i] - 1];
                a[y[i] - 1] = temp;
                count++;
                sorted = isSorted(a);
                if (sorted) {
                    break;
                }
            }
        }
        System.out.println(count);
    }

    public static boolean isSorted(int[] a) {
        for (int i = 0; i < a.length - 1; i++) {
            if (a[i] > a[i + 1]) {
                return false;
            }
        }
        return true;
    }
}
