#include <stdio.h>
#include <stdlib.h>

#define N 405
#define M 100005

int main() {
    int n, m, i, j, k, x, y, cnt, ans;
    int a[N][N], b[N][N];

    scanf("%d %d", &n, &m);
    for (i = 1; i <= m; i++) {
        scanf("%d %d", &x, &y);
        a[x][y] = a[y][x] = 1;
    }

    for (i = 1; i <= n; i++) {
        for (j = 1; j <= n; j++) {
            if (i != j) {
                for (k = 1; k <= m; k++) {
                    if (a[i][k] && a[j][k]) {
                        b[i][j] = b[j][i] = 1;
                        break;
                    }
                    if (a[i][k] || a[j][k]) {
                        b[i][j] = b[j][i] = 1;
                        break;
                    }
                }
            }
        }
    }

    ans = 0;
    for (i = 1; i <= n; i++) {
        for (j = i + 1; j <= n; j++) {
            if (b[i][j]) {
                ans++;
            }
        }
    }

    printf("%d\n", ans);
    return 0;
}
