#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 405;
const int M = 100005;

int n, m;
int x[M], y[M];
int dp[N][N];
int ans;

int main() {
    cin >> n >> m;
    for (int i = 1; i <= m; i++) {
        cin >> x[i] >> y[i];
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            dp[i][j] = 1;
        }
    }
    for (int i = 1; i <= m; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                if (j != k) {
                    dp[j][k] = (dp[j][k] + dp[j][x[i]] * dp[k][y[i]]) / 2;
                    dp[j][k] = (dp[j][k] + dp[j][y[i]] * dp[k][x[i]]) / 2;
                    dp[j][k] = (dp[j][k] + dp[j][x[i]] * dp[k][x[i]]) / 2;
                    dp[j][k] = (dp[j][k] + dp[j][y[i]] * dp[k][y[i]]) / 2;
                }
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = i + 1; j <= n; j++) {
            if (dp[i][j] > 0) {
                ans++;
            }
        }
    }
    cout << ans << endl;
    return 0;
}
