#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007
#define N 16

typedef long long ll;

ll dp[N][1 << 15];
ll dp2[N][1 << 15];

void add(ll *a, ll b) {
    *a = (*a + b) % MOD;
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    int x, y;
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &x, &y);
        x--; y--;
    }

    for (int i = 0; i < n; i++) {
        for (int mask = 0; mask < (1 << n); mask++) {
            dp[i][mask] = 0;
            dp2[i][mask] = 0;
        }
    }

    dp[0][1] = 1;
    for (int i = 0; i < n; i++) {
        for (int mask = 0; mask < (1 << n); mask++) {
            if (dp[i][mask] == 0) continue;
            for (int j = 0; j < n; j++) {
                if (j == i) continue;
                if ((mask >> j) & 1) continue;
                int new_mask = mask | (1 << j);
                if (i == n - 1) {
                    add(&dp2[j][new_mask], dp[i][mask]);
                } else {
                    add(&dp[j][new_mask], dp[i][mask]);
                }
            }
        }
        for (int mask = 0; mask < (1 << n); mask++) {
            dp[i][mask] = dp2[i][mask];
            dp2[i][mask] = 0;
        }
    }

    ll ans = 0;
    for (int mask = 0; mask < (1 << n); mask++) {
        ans = (ans + dp[n - 1][mask]) % MOD;
    }

    printf("%lld\n", ans);

    return 0;
}
