import sys
input = sys.stdin.readline

mod = 10**9 + 7
N = 15

def pow_mod(a, b):
    res = 1
    while b:
        if b & 1:
            res = res * a % mod
        a = a * a % mod
        b >>= 1
    return res

def C(n, k):
    if k < 0 or k > n:
        return 0
    return fac[n] * inv[k] % mod * inv[n-k] % mod

fac = [1] * (N + 1)
for i in range(1, N + 1):
    fac[i] = fac[i-1] * i % mod

inv = [0] * (N + 1)
inv[N] = pow_mod(fac[N], mod - 2)
for i in range(N - 1, -1, -1):
    inv[i] = inv[i+1] * (i+1) % mod

def solve():
    n, m = map(int, input().split())
    g = [[] for _ in range(n + 1)]
    for _ in range(m):
        u, v = map(int, input().split())
        g[u].append(v)

    dp = [[0] * (1 << n) for _ in range(n + 1)]
    dp[1][1] = 1
    for i in range(1, n + 1):
        for mask in range(1 << n):
            if mask & (1 << (i - 1)):
                for j in g[i]:
                    dp[j][mask | (1 << (j - 1))] = (dp[j][mask | (1 << (j - 1))] + dp[i][mask]) % mod
            else:
                dp[i][mask] = (dp[i][mask] + dp[i][mask & ~(1 << (i - 1))]) % mod

    ans = 0
    for mask in range(1 << n):
        if mask & (1 << 0) and mask & (1 << 1):
            continue
        if mask & (1 << 0):
            ans = (ans + dp[2][mask]) % mod
        else:
            ans = (ans + dp[2][mask]) % mod

    print(ans)

T = 1
for _ in range(T):
    solve()
