#include <stdio.h>
#include <stdlib.h>

#define MAX_RATING 4800

int main() {
    int n, i, rating, min_colors, max_colors, colors[8] = {0};
    int color_map[MAX_RATING + 1];

    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d", &rating);
        if (rating <= 399) {
            color_map[rating] = 0;
        } else if (rating <= 799) {
            color_map[rating] = 1;
        } else if (rating <= 1199) {
            color_map[rating] = 2;
        } else if (rating <= 1599) {
            color_map[rating] = 3;
        } else if (rating <= 1999) {
            color_map[rating] = 4;
        } else if (rating <= 2399) {
            color_map[rating] = 5;
        } else if (rating <= 2799) {
            color_map[rating] = 6;
        } else if (rating <= 3199) {
            color_map[rating] = 7;
        } else {
            color_map[rating] = -1;
        }
    }

    for (i = 1; i <= MAX_RATING; i++) {
        if (color_map[i] != -1) {
            colors[color_map[i]] = 1;
        }
    }

    min_colors = 0;
    for (i = 0; i < 8; i++) {
        if (colors[i] == 1) {
            min_colors++;
        }
    }

    max_colors = 8;
    for (i = 0; i < 8; i++) {
        if (colors[i] == 1) {
            max_colors--;
        }
    }

    printf("%d %d\n", min_colors, max_colors);

    return 0;
}
