#include <iostream>
#include <set>
#include <vector>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> a[i];
    }

    std::set<int> uniqueColors;
    for (int i = 0; i < n; ++i) {
        if (a[i] <= 399) {
            uniqueColors.insert(0);
        } else if (a[i] <= 799) {
            uniqueColors.insert(1);
        } else if (a[i] <= 1199) {
            uniqueColors.insert(2);
        } else if (a[i] <= 1599) {
            uniqueColors.insert(3);
        } else if (a[i] <= 1999) {
            uniqueColors.insert(4);
        } else if (a[i] <= 2399) {
            uniqueColors.insert(5);
        } else if (a[i] <= 2799) {
            uniqueColors.insert(6);
        } else if (a[i] <= 3199) {
            uniqueColors.insert(7);
        } else {
            uniqueColors.insert(a[i] % 8);
        }
    }

    std::cout << uniqueColors.size() << " " << std::max(uniqueColors.size(), 1) << std::endl;

    return 0;
}
