#include <stdio.h>
#include <math.h>

int main() {
    int n;
    double debt = 100000;
    double interest = 0.05;

    scanf("%d", &n);

    for (int i = 0; i < n; i++) {
        debt = ceil(debt + (debt * interest) / 100) / 1000 * 1000;
    }

    printf("%d\n", (int)debt);

    return 0;
}
