#include <stdio.h>
#include <string.h>

int main() {
    int n;
    char s[105];
    scanf("%d %s", &n, s);

    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == '(') cnt++;
        else cnt--;
        if (cnt < 0) {
            cnt++;
            s[i] = '(';
        }
    }

    int i = 0;
    while (i < n && s[i] == '(') i++;
    int j = n - 1;
    while (j >= 0 && s[j] == ')') j--;

    if (i > j) {
        for (int k = 0; k < n; k++) {
            s[k] = '(';
        }
        s[n] = ')';
    } else {
        for (int k = 0; k < i; k++) {
            s[k] = '(';
        }
        for (int k = j + 1; k < n; k++) {
            s[k] = ')';
        }
        for (int k = i; k <= j; k++) {
            s[k] = '(';
        }
    }

    s[n] = '\0';
    printf("%s\n", s);

    return 0;
}
