#include <iostream>
#include <string>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;
    std::string s;
    std::cin >> s;

    int open = 0;
    int close = 0;
    for (char c : s) {
        if (c == '(') open++;
        else close++;
    }

    if (open == 0 && close == 0) {
        std::cout << "()";
    } else if (open == 0) {
        std::cout << std::string(close, '(') << std::string(close, ')');
    } else if (close == 0) {
        std::cout << std::string(open, '(') << std::string(open, ')');
    } else {
        std::string ans = std::string(open, '(') + std::string(close, ')');
        int i = 0;
        while (i < n && s[i] == '(') i++;
        int j = n - 1;
        while (j >= 0 && s[j] == ')') j--;
        if (i > j) {
            std::cout << ans;
        } else {
            std::cout << std::string(i, '(') << ans << std::string(n - j - 1, ')');
        }
    }

    return 0;
}
